import React, { useSelector, useEffect } from 'react'
import { useForm } from 'react-hook-form';
import { crearCliente, updateCliente } from '../../_actions/clientes';
import { useDispatch } from 'react-redux';

export default function FormCliente({ getId, setId }) {
	const { register, handleSubmit, reset, setValue } = useForm();
	const cliente = useSelector(state => state.clientes.find((cliente) => cliente._id === getId ? cliente : null))
	
	const onSubmit = (data) => {
		if (getId === 0) {
			dispatch(crearCliente(data))
			reset()
		} else {
			dispatch(updateCliente(getId, data))
			reset()
			setId(0)
		}
	}
	return (
		<>
			<form onSubmit={handleSubmit(onSubmit)}>
				<div>
					<input type="text" name="nombre" placeholder="Nombre" ref={register} />
				</div>
				<div>
					<input type="text" name="apellido" placeholder="Apellido" ref={register} />
				</div>
				<div>
					<input type="text" name="dni" placeholder="DNI" ref={register} />
				</div>
				<button type="submit">Submit</button>
			</form>
		</>
	)
}
